mapper.select:
SELECT
    date, SUM(pv) AS pv, SUM(uv) AS uv, SUM(uip) AS uip
FROM
    t_link_access_stats
WHERE
    full_short_url = #{param.fullShortUrl}
  AND gid = #{param.gid}
  AND date BETWEEN #{param.startDate}
  and #{param.endDate}
GROUP BY
    full_short_url, gid, date;

SELECT
    hour, SUM(pv) AS pv
FROM
    t_link_access_stats
WHERE
    full_short_url = #{param.fullShortUrl}
  AND gid = #{param.gid}
  AND date BETWEEN #{param.startDate}
  and #{param.endDate}
GROUP BY
    full_short_url, gid, hour;

SELECT weekday,
       SUM(pv) AS pv
FROM t_link_access_stats
WHERE full_short_url = #{param.fullShortUrl}
  AND gid = #{param.gid}
  AND date BETWEEN #{param.startDate}
  and #{param.endDate}
GROUP BY
    full_short_url, gid, weekday;
