mapper.select:
SELECT browser,
       SUM(cnt) AS count
FROM
    t_link_browser_stats
WHERE
    full_short_url = #{param.fullShortUrl}
  AND gid = #{param.gid}
  AND date BETWEEN #{param.startDate}
  and #{param.endDate}
GROUP BY
    full_short_url, gid, browser;

SELECT browser,
       SUM(cnt) AS count
FROM t_link_browser_stats
WHERE gid = #{param.gid}
  AND date BETWEEN #{param.startDate} and #{param.endDate}
GROUP BY gid, browser;
